import React, {useState} from 'react';
import axios from "axios";
import "../Header/header.css"

const Modal = ({modalActive, setModalActive}) => {
    const [questions, setQuestions] = useState({
        name: '',
        number: '',
        text: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);
    const config = {
        headers: {
            Authorization: 'Token 20c336c50442572e6621017043e9fbc4801ea5a8'
        }
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        axios
            .post('https://coins-e528efb62dd4.herokuapp.com/review/', questions, config)
            .then((response) => {
                console.log(response);
                setIsSubmitted(true);
            })
            .catch((error) => {
                console.error(error);
            });
    };
    const handleSubmitModal = () =>{
        setModalActive(false) || setIsSubmitted(true)
    }
    return (
        <div>
            {
                modalActive && (
                    <div className='modal__one' onClick={() => setModalActive(false)}>
                        <div className='modal_content'
                             onClick={(event) => event.stopPropagation()}>
                            <div className="names__two">
                                {isSubmitted ? (
                                    <div className="isSubmitted">
                                        <h1>Ваша заявка отправлена</h1>
                                        <p>Мы свяжемся с вами в ближайшее время.</p>
                                    </div>
                                ) : (
                                    ""
                                )}
                                <div>
                                    <h1 className="two_one">Остались вопросы?</h1>
                                    <p>Напишите их нам, и мы постараемся ответить <br/> на них как можно скорее.</p>
                                    <form className="modal_form" onSubmit={handleSubmit}>
                                        <div className="form-item">
                                            <label htmlFor="name">Как к вам обращаться?</label>
                                            <input
                                                type="text"
                                                id="name"
                                                autoComplete="off"
                                                onChange={(e) => setQuestions({...questions, name: e.target.value})}
                                                required
                                            />
                                        </div>
                                        <div className="form-item">
                                            <label htmlFor="tel">Ваш контактный телефон</label>
                                            <input
                                                type="tel"
                                                id="tel"
                                                autoComplete="off"
                                                onChange={(e) => setQuestions({
                                                    ...questions,
                                                    number: e.target.value
                                                })}
                                                required
                                            />
                                        </div>
                                        <div className="form-item">
                                            <label htmlFor="form_field_id">Задайте Ваш вопрос здесь</label>
                                            <textarea
                                                id="form_field_id"
                                                autoComplete="off"
                                                onChange={(e) => setQuestions({...questions, text: e.target.value})}
                                                required
                                            />
                                        </div>
                                        <button onClick={handleSubmitModal} className="submit--btn">
                                            <span>Отправить</span>
                                        </button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                )
            }
        </div>

    );
};

export default Modal;
