import React, {useRef, useState} from 'react';
import img from '../../img/L.png'
import {Link} from 'react-scroll'
import Modal from "../Modal/Modal";

    const Header = () => {
        const [modalActive, setModalActive] = useState(false);
        const [isMenuOpen, setMenuOpen] = useState(false);

        const toggleMenu = () => {
            setMenuOpen(!isMenuOpen);
        };
        return (
            <div id="header">
                <div className="container">
                    <div className="navbar">
                        <div className="logo">
                            <img src={img} alt="" />
                        </div>
                        <div className={`numbar ${isMenuOpen ? 'active' : ''}`}>
                            <ul>
                                <li>
                                    <Link className="test6" to="about" spy={true} smooth={true} duration={500} onClick={toggleMenu}>
                                        Курсы
                                    </Link>
                                </li>
                                <li>
                                    <Link
                                        className="test6"
                                        to="our"
                                        spy={true}
                                        smooth={true}
                                        duration={500}
                                        onClick={toggleMenu}
                                    >
                                        Преимущества
                                    </Link>
                                </li>
                                <li>
                                    <Link
                                        className="test6"
                                        to="block"
                                        spy={true}
                                        smooth={true}
                                        duration={500}
                                        onClick={toggleMenu}
                                    >
                                        Условия
                                    </Link>
                                </li>
                                <li>
                                    <Link className="test6" to="about_us" spy={true} smooth={true} duration={500} onClick={toggleMenu}>
                                        FAQ
                                    </Link>
                                </li>
                                <li>
                                    <Link
                                        className="test6"
                                        to="footer"
                                        spy={true}
                                        smooth={true}
                                        duration={500}
                                        onClick={toggleMenu}
                                    >
                                        Контакты
                                    </Link>
                                </li>
                            </ul>
                            <button onClick={() => setModalActive(true)} className="btn">
                                <span className="span">Оставить заявку</span>
                            </button>
                        </div>
                        <div className={`burger ${isMenuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                            <div className="line1"></div>
                            <div className="line2"></div>
                            <div className="line3"></div>
                        </div>
                    </div>

                    <Modal active={modalActive} setActive={setModalActive} />
                </div>
            </div>
        );
    };

export default Header;
